def shortest_path_after_queries(n, queries):
    # Initialize the graph with the initial roads
    graph = {i: [i + 1] for i in range(n - 1)}

    # Function to perform BFS and find the shortest path from 0 to n-1
    def bfs():
        queue = deque([0])
        visited = set([0])
        distance = 0

        while queue:
            for _ in range(len(queue)):
                current = queue.popleft()
                if current == n - 1:
                    return distance
                for neighbor in graph[current]:
                    if neighbor not in visited:
                        visited.add(neighbor)
                        queue.append(neighbor)
            distance += 1

    # List to store the results
    result = []

    # Process each query
    for u, v in queries:
        # Add the new road to the graph
        if u not in graph:
            graph[u] = []
        graph[u].append(v)

        # Find the shortest path after adding the new road
        result.append(bfs())

    return result

