    G_dict = defaultdict(set)
    H_dict = defaultdict(set)

    for u, v in G:
        G_dict[u].add(v)
        G_dict[v].add(u)

    for a, b in H:
        H_dict[a].add(b)
        H_dict[b].add(a)

    @lru_cache(None)
    def dp(i, perm):
        if i == N:
            return 0

        min_cost = float('inf')

        for j in range(1, N + 1):
            if j not in perm:
                new_perm = perm + (j,)
                if (i + 1, j) in A:
                    cost = A[(i + 1, j)]
                else:
                    cost = 0

                if j in G_dict[i + 1]:
                    if j in H_dict[new_perm.index(j) + 1]:
                        min_cost = min(min_cost, dp(i + 1, new_perm))
                    else:
                        min_cost = min(min_cost, cost + dp(i + 1, new_perm))
                else:
                    if j in H_dict[new_perm.index(j) + 1]:
                        min_cost = min(min_cost, cost + dp(i + 1, new_perm))
                    else:
                        min_cost = min(min_cost, 2 * cost + dp(i + 1, new_perm))

        return min_cost

