def min_time_to_reduce_mountain(mountainHeight, workerTimes):
    def is_possible(time):
        remaining_height = mountainHeight
        for workerTime in workerTimes:
            x = 1
            while True:
                current_time = workerTime * (1 + x) * x // 2
                if current_time > time:
                    break
                if remaining_height >= x:
                    remaining_height -= x
                x += 1
            if remaining_height == 0:
                return True
        return False

    low, high = 0, max(workerTimes) * (mountainHeight * (mountainHeight + 1)) // 2
    while low < high:
        mid = (low + high) // 2
        if is_possible(mid):
            high = mid
        else:
            low = mid + 1
    return low

