def minValidStrings(words, target):
    from collections import defaultdict

    # Step 1: Create a dictionary of prefixes
    prefixes = defaultdict(list)
    for word in words:
        for i in range(1, len(word) + 1):
            prefixes[word[:i]].append(word)

    # Step 2: Initialize the DP array
    dp = [float('inf')] * (len(target) + 1)
    dp[0] = 0

    # Step 3: Fill the DP array
    for i in range(1, len(target) + 1):
        for prefix in prefixes:
            if target[i - len(prefix):i] == prefix:
                dp[i] = min(dp[i], dp[i - len(prefix)] + 1)

    # Step 4: Return the result
    return dp[len(target)] if dp[len(target)] != float('inf') else -1

