def minFlips(grid):
    m, n = len(grid), len(grid[0])

    def flips_to_palindrome(arr):
        return sum(arr[i] != arr[~i] for i in range((len(arr) + 1) // 2))

    row_flips = sum(flips_to_palindrome(row) for row in grid)
    col_flips = sum(flips_to_palindrome([grid[i][j] for i in range(m)]) for j in range(n))

    return min(row_flips, col_flips)

