def countSubstrings(word: str, k: int) -> int:
    vowels = set('aeiou')
    n = len(word)
    count = 0

    for i in range(n):
        vowel_count = 0
        consonant_count = 0
        char_set = set()

        for j in range(i, n):
            char = word[j]
            if char in vowels:
                vowel_count += 1
                char_set.add(char)
            else:
                consonant_count += 1

            if len(char_set) == 5 and consonant_count == k:
