def minTime(mountainHeight, workerTimes):
    def canReduce(height, targetTime):
        remainingHeight = height
        for time in workerTimes:
            x = 1
            while True:
                workTime = time * (x * (x + 1)) // 2
                if workTime > targetTime:
                    break
                if remainingHeight >= x:
                    remainingHeight -= x
                x += 1
            if remainingHeight == 0:
                return True
        return False

    left, right = 1, sum(time * (time + 1) // 2 for time in workerTimes) * mountainHeight
    while left < right:
        mid = (left + right) // 2
        if canReduce(mountainHeight, mid):
            right = mid
        else:
            left = mid + 1
    return left

