def min_prefixes_to_form_target(words, target):
    from collections import defaultdict

    # Step 1: Preprocess words into a dictionary of prefixes
    prefix_dict = defaultdict(list)
    for word in words:
        for i in range(1, len(word) + 1):
            prefix_dict[word[:i]].append(word)

    # Step 2: Initialize DP array
    dp = [float('inf')] * len(target)
    dp[0] = 1 if target[0] in prefix_dict else float('inf')

    # Step 3: Fill the DP array
    for i in range(1, len(target)):
        for j in range(i + 1):
            prefix = target[j:i + 1]
            if prefix in prefix_dict:
                dp[i] = min(dp[i], dp[j - 1] + 1 if j > 0 else 1)

    # Step 4: Return the result
    return dp[-1] if dp[-1] != float('inf') else -1

