def min_transformations(S, T):
    n = len(S)
    dp = [[float('inf')] * (n + 1) for _ in range(n + 1)]
    dp[0][0] = 0
    prev = [[None] * (n + 1) for _ in range(n + 1)]

    for i in range(1, n + 1):
        dp[i][0] = i
        prev[i][0] = (i - 1, 0)
    for j in range(1, n + 1):
        dp[0][j] = j
        prev[0][j] = (0, j - 1)

    for i in range(1, n + 1):
        for j in range(1, n + 1):
            if S[i - 1] == T[j - 1]:
                cost = 0
            else:
                cost = 1

            if dp[i - 1][j - 1] + cost < dp[i][j]:
                dp[i][j] = dp[i - 1][j - 1] + cost
                prev[i][j] = (i - 1, j - 1)
            elif dp[i - 1][j] + 1 < dp[i][j]:
                dp[i][j] = dp[i - 1][j] + 1
                prev[i][j] = (i - 1, j)
            elif dp[i][j - 1] + 1 < dp[i][j]:
                dp[i][j] = dp[i][j - 1] + 1
                prev[i][j] = (i, j - 1)

    path = []
    i, j = n, n
    while i > 0 or j > 0:
        path.append((i, j))
        i, j = prev[i][j]
    path.reverse()

    result = [S[:i]]
    for (i, j), (k, l) in zip(path[:-1], path[1:]):
        if k == i - 1 and l == j - 1:
            continue
        elif k == i - 1 and l == j:
            result.append(S[:i] + T[l:j])
        elif k == i and l == j - 1:
            result.append(T[k:i] + S[:j])

    return result

