                prev[i][j] = (i - 1, j - 1)
            elif dp[i - 1][j] + 1 < dp[i][j]:
                dp[i][j] = dp[i - 1][j] + 1
                prev[i][j] = (i - 1, j)
            elif dp[i][j - 1] + 1 < dp[i][j]:
                dp[i][j] = dp[i][j - 1] + 1
                prev[i][j] = (i, j - 1)

    path = []
    i, j = n, n
    while i > 0 or j > 0:
        path.append((i, j))
        i, j = prev[i][j]
    path.reverse()

    result = [S[:i]]
    for (i, j), (k, l) in zip(path[:-1], path[1:]):
        if k == i - 1 and l == j - 1:
            continue
        elif k == i - 1 and l == j:
            result.append(S[:i] + T[l:j])
        elif k == i and l == j - 1:
            result.append(T[k:i] + S[:j])

    return result

