def countWinningPlayers(n, pick):
    # Dictionary to store the count of balls picked by each player for each color
    player_balls = {i: {} for i in range(n)}

    # Update the dictionary with the picks
    for p in pick:
        player_id, color = p
        if color not in player_balls[player_id]:
            player_balls[player_id][color] = 0
        player_balls[player_id][color] += 1

    # Count the number of players who win
    winning_players = 0
    for i in range(n):
        for color, count in player_balls[i].items():
            if count > i:
                winning_players += 1
                break

    return winning_players

