def find_kth_character(k):
    word = "a"
    length = 1

    while length < k:
        new_word = ""
        for char in word:
            if char == 'z':
                new_word += 'a'
            else:
                new_word += chr(ord(char) + 1)
        word += new_word
        length += len(new_word)

    return word[k-1]

