def minPrefixesToFormTarget(words, target):
    from collections import defaultdict
    import math

    # Create a dictionary to store words by their prefix lengths
    prefix_dict = defaultdict(list)
    for word in words:
        for i in range(1, len(word) + 1):
            prefix_dict[word[:i]].append(word)

    # Initialize the DP array with infinity
    dp = [math.inf] * (len(target) + 1)
    dp[0] = 0

    # Fill the DP array
    for i in range(1, len(target) + 1):
        for prefix in prefix_dict:
            if i >= len(prefix) and target[i - len(prefix):i] == prefix:
                dp[i] = min(dp[i], dp[i - len(prefix)] + 1)

    # Return the result
    return dp[len(target)] if dp[len(target)] != math.inf else -1

