def countValidSubstrings(s: str, k: int) -> int:
    n = len(s)
    zero_count = one_count = 0
    left = 0
    result = 0

    for right in range(n):
        if s[right] == '0':
            zero_count += 1
        else:
            one_count += 1

        while zero_count > k and one_count > k:
            if s[left] == '0':
                zero_count -= 1
            else:
                one_count -= 1
            left += 1

        result += right - left + 1

    return result

