    def dfs(i, j, health):
        if i < 0 or i >= len(grid) or j < 0 or j >= len(grid[0]) or health <= 0:
            return False
        if i == len(grid) - 1 and j == len(grid[0]) - 1:
            return health >= 1

        temp = grid[i][j]
        if temp == 1:
            health -= 1
        else:
            grid[i][j] = 1

        if dfs(i + 1, j, health) or dfs(i - 1, j, health) or dfs(i, j + 1, health) or dfs(i, j - 1, health):
            return True

        if temp == 1:
            health += 1
        else:
            grid[i][j] = temp

        return False

    return dfs(0, 0, health)

