def vertical_text():
    import sys
    input = sys.stdin.read
    data = input().split()

    N = int(data[0])
    strings = data[1:]

    # Determine the maximum length
    M = max(len(s) for s in strings)

    # Initialize the vertical strings
    vertical_strings = [''] * M

    # Fill the vertical strings
    for i in range(M):
        for j in range(N):
            if i < len(strings[j]):
                vertical_strings[i] += strings[j][i]
            else:
                vertical_strings[i] += '*'

    # Ensure no string ends with '*'
    for i in range(M):
        if vertical_strings[i].endswith('*'):
            vertical_strings[i] = vertical_strings[i][:-1]

    # Print the result
    for s in vertical_strings:
        print(s)

