def countWinningPlayers(n, pick):
    # Initialize a dictionary to keep track of the count of balls each player has picked for each color
    player_counts = {i: {} for i in range(n)}

    # Update the counts based on the picks
    for player, color in pick:
        if color not in player_counts[player]:
            player_counts[player][color] = 0
        player_counts[player][color] += 1

    # Count the number of players who win
    winning_players = 0
    for player, colors in player_counts.items():
        for color_count in colors.values():
            if color_count >= player + 1:
                winning_players += 1
                break

    return winning_players

