def count_substrings(s: str, k: int) -> int:
    n = len(s)
    total_count = 0

    # Check all possible substring lengths
    for length in range(1, n + 1):
        zero_count = one_count = 0

        # Initialize the first window
        for i in range(length):
            if s[i] == '0':
                zero_count += 1
            else:
                one_count += 1

        # If the current window satisfies the k-constraint, add it to the count
        if zero_count <= k or one_count <= k:
            total_count += 1

        # Slide the window across the string
        for start in range(1, n - length + 1):
            end = start + length - 1

            # Update the counts for the new window
            if s[start - 1] == '0':
                zero_count -= 1
            else:
                one_count -= 1

            if s[end] == '0':
                zero_count += 1
            else:
                one_count += 1

            # If the new window satisfies the k-constraint, add it to the count
            if zero_count <= k or one_count <= k:
                total_count += 1

    return total_count

