def modifyArray(nums, k, multiplier):
    for _ in range(k):
        min_val = min(nums)
        min_index = nums.index(min_val)
        nums[min_index] = min_val * multiplier
    return nums

def min_sum_of_digits(nums):
    def sum_of_digits(n):
        return sum(int(digit) for digit in str(n))

    modified_nums = [sum_of_digits(num) for num in nums]
    return min(modified_nums)

