def minimumTime(mountainHeight, workerTimes):
    workerTimes.sort()
    left, right = 0, 10**14  # Upper bound is a large number to ensure we find the minimum

    while left < right:
        mid = (left + right) // 2
        total_time = 0
        for time in workerTimes:
            x = 1
            while time * (x * (x + 1) // 2) <= mid:
                total_time += time * x
                x += 1
        if total_time >= mountainHeight * (mountainHeight + 1) // 2:
            right = mid
        else:
            left = mid + 1

    return left

