def minValidStrings(words, target):
    from collections import defaultdict
    import math

    # Create a dictionary to store prefixes of each word
    prefix_dict = defaultdict(list)
    for word in words:
        for i in range(1, len(word) + 1):
            prefix_dict[word[:i]].append(i)

    # Initialize the DP array with infinity
    dp = [math.inf] * (len(target) + 1)
    dp[0] = 0

    # Fill the DP array
    for i in range(1, len(target) + 1):
        for prefix, lengths in prefix_dict.items():
            if i >= len(prefix) and target[i - len(prefix):i] == prefix:
                for length in lengths:
                    dp[i] = min(dp[i], dp[i - length] + 1)

    # Return the result
    return dp[len(target)] if dp[len(target)] != math.inf else -1

