def count_substrings(word: str, k: int) -> int:
    vowels = set('aeiou')
    n = len(word)
    count = 0

    # Iterate over possible starting points of the substring
    for i in range(n):
        vowel_count = {'a': 0, 'e': 0, 'i': 0, 'o': 0, 'u': 0}
        consonant_count = 0

        # Iterate over possible ending points of the substring
        for j in range(i, n):
            char = word[j]
            if char in vowels:
                vowel_count[char] += 1
            elif char.isalpha():
                consonant_count += 1

            # Check if the current window has exactly k consonants and all vowels
            if consonant_count == k and all(vowel_count[v] > 0 for v in vowels):
                count += 1

    return count

