        swapped_numbers = set()
        num_list = list(num_str)
        n = len(num_list)
        for i in range(n):
            for j in range(i + 1, n):
                num_list[i], num_list[j] = num_list[j], num_list[i]
                swapped_numbers.add(''.join(num_list))
                num_list[i], num_list[j] = num_list[j], num_list[i]
        return swapped_numbers

    num_set = set(map(str, nums))
    count = 0

    for num_str in map(str, nums):
        swapped_numbers = generate_swapped_numbers(num_str)
        for swapped_num in swapped_numbers:
            if swapped_num in num_set and swapped_num != num_str:
                count += 1

    # Each pair is counted twice, so divide by 2
    return count // 2

