def minTime(mountainHeight, workerTimes):
    def canComplete(time):
        remainingHeight = mountainHeight
        for t in workerTimes:
            k = 1
            while True:
                workDone = t * k * (k + 1) // 2
                if workDone > time:
                    break
                remainingHeight -= k
                if remainingHeight <= 0:
                    return True
                k += 1
        return False

    low, high = max(workerTimes), sum(t * (t + 1) // 2 for t in workerTimes)
    while low < high:
        mid = (low + high) // 2
        if canComplete(mid):
            high = mid
        else:
            low = mid + 1
    return low

