    m, n = len(grid), len(grid[0])

    def flips_needed(arr):
        return sum(1 for i in range((len(arr) + 1) // 2) if arr[i] != arr[-i - 1])

    row_flips = sum(flips_needed(row) for row in grid)
    col_flips = sum(flips_needed([grid[j][i] for j in range(m)]) for i in range(n))

    return min(row_flips, col_flips)

