def shortest_path_after_queries(n, queries):
    graph = [[] for _ in range(n)]
    for i in range(n - 1):
        graph[i].append(i + 1)

    def bfs(start, end):
        queue = deque([start])
        visited = [False] * n
        visited[start] = True
        distance = 0

        while queue:
            for _ in range(len(queue)):
                current = queue.popleft()
                if current == end:
                    return distance
                for neighbor in graph[current]:
                    if not visited[neighbor]:
                        visited[neighbor] = True
                        queue.append(neighbor)
            distance += 1

    result = []
    for u, v in queries:
        graph[u].append(v)
        result.append(bfs(0, n - 1))

    return result

