def minimumTime(mountainHeight, workerTimes):
    def canReduce(height, time):
        totalWork = 0
        for worker in workerTimes:
            x = 1
            while worker * (x * (x + 1) // 2) <= time:
                if worker * x <= height:
                    totalWork += 1
                x += 1
            if totalWork >= height:
                return True
        return False

    left, right = 1, max(workerTimes) * mountainHeight * (mountainHeight + 1) // 2
    while left < right:
        mid = (left + right) // 2
        if canReduce(mountainHeight, mid):
            right = mid
        else:
            left = mid + 1
