def count_valid_substrings(s: str, k: int) -> int:
    n = len(s)
    count = 0
    left = 0
    zero_count = 0
    one_count = 0

    for right in range(n):
        if s[right] == '0':
            zero_count += 1
        else:
            one_count += 1

        while zero_count > k and one_count > k:
            if s[left] == '0':
                zero_count -= 1
            else:
                one_count -= 1
            left += 1

        count += right - left + 1

    return count

