def minTime(mountainHeight, workerTimes):
    def canReduce(height, t):
        total_time = 0
        for time in workerTimes:
            x = 1
            while time * x <= t:
                if height >= x:
                    total_time += time * x
                    height -= x
                x += 1
                if height == 0:
                    break
            if height > 0:
                return False
        return True

    left, right = 1, mountainHeight * max(workerTimes)
    while left < right:
        mid = (left + right) // 2
        if canReduce(mountainHeight, mid):
            right = mid
        else:
            left = mid + 1
    return left

