def minConcatenatedStrings(words, target):
    from collections import defaultdict

    # Create a dictionary to store prefixes of words
    prefix_map = defaultdict(list)
    for word in words:
        for i in range(1, len(word) + 1):
            prefix_map[word[:i]].append(word)

    # Initialize the dp array
    dp = [float('inf')] * (len(target) + 1)
    dp[0] = 0

    # Fill the dp array
    for i in range(1, len(target) + 1):
        for prefix in prefix_map:
            if i >= len(prefix) and target[i - len(prefix):i] == prefix:
                dp[i] = min(dp[i], dp[i - len(prefix)] + 1)

    # If dp[len(target)] is still infinity, it means it's not possible to form the target
    return dp[len(target)] if dp[len(target)] != float('inf') else -1

