def minTime(mountainHeight, workerTimes):
    def canReduce(height, time):
        remainingHeight = height
        for wt in sorted(workerTimes, reverse=True):
            if remainingHeight == 0:
                break
            x = 1
            while True:
                current_time = wt * x * (x + 1) // 2
                if current_time > time:
                    break
                if remainingHeight >= x:
                    remainingHeight -= x
                x += 1
        return remainingHeight == 0

    left, right = max(workerTimes), sum(workerTimes) * (mountainHeight * (mountainHeight + 1)) // 2
    while left < right:
        mid = (left + right) // 2
        if canReduce(mountainHeight, mid):
            right = mid
        else:
            left = mid + 1
    return left

