def min_prefixes_to_form_target(words, target):
    from collections import defaultdict

    # Create a dictionary of prefixes to words
    prefix_dict = defaultdict(list)
    for word in words:
        for i in range(1, len(word) + 1):
            prefix_dict[word[:i]].append(word)

    # Initialize the DP array
    dp = [float('inf')] * (len(target) + 1)
    dp[0] = 0

    # Fill the DP array
    for i in range(len(target)):
        for prefix in prefix_dict:
            if target.startswith(prefix, i):
                dp[i + len(prefix)] = min(dp[i + len(prefix)], dp[i] + 1)

    return dp[-1] if dp[-1] != float('inf') else -1

