def final_position(n, commands):
    row, col = 0, 0
    for cmd in commands:
        if cmd == "UP":
            row -= 1
        elif cmd == "RIGHT":
            col += 1
        elif cmd == "DOWN":
            row += 1
        elif cmd == "LEFT":
            col -= 1
        # Ensure the snake stays within the grid boundaries
        row = max(0, min(row, n-1))
        col = max(0, min(col, n-1))
    return row * n + col

