def canReachFinalCell(grid, health):
    def dfs(x, y, health):
        if x < 0 or x >= len(grid) or y < 0 or y >= len(grid[0]) or health <= 0:
            return False
        if x == len(grid) - 1 and y == len(grid[0]) - 1:
            return health >= 1

        temp = grid[x][y]
        grid[x][y] = 2  # Mark as visited

        if dfs(x + 1, y, health - temp) or dfs(x - 1, y, health - temp) or dfs(x, y + 1, health - temp) or dfs(x, y - 1, health - temp):
            return True

        grid[x][y] = temp  # Unmark as visited
        return False

    return dfs(0, 0, health)

