def minTime(mountainHeight, workerTimes):
    def canReduce(height, time):
        remainingHeight = height
        for wt in workerTimes:
            x = 1
            while True:
                workTime = wt * x * (x + 1) // 2
                if workTime > time:
                    break
                if remainingHeight >= x:
                    remainingHeight -= x
                x += 1
            if remainingHeight == 0:
                return True
        return False

    left, right = 1, max(workerTimes) * mountainHeight * (mountainHeight + 1) // 2
    while left < right:
        mid = (left + right) // 2
        if canReduce(mountainHeight, mid):
            right = mid
        else:
            left = mid + 1
    return left

