def min_valid_strings(words, target):
    from collections import defaultdict

    # Create a dictionary to store the prefixes of each word
    prefix_dict = defaultdict(list)
    for word in words:
        for i in range(1, len(word) + 1):
            prefix_dict[word[:i]].append(word)

    # Initialize the dp array
    dp = [float('inf')] * (len(target) + 1)
    dp[0] = 0

    # Iterate through each character of the target string
    for i in range(1, len(target) + 1):
        for prefix in prefix_dict:
            if target[i-1] == prefix[-1] and i - len(prefix) >= 0:
                dp[i] = min(dp[i], dp[i - len(prefix)] + 1)

    # If it is not possible to form the target string, return -1
    return dp[-1] if dp[-1] != float('inf') else -1

