import sys
from itertools import permutations

def read_input():
    stdin = sys.stdin.read
    data = stdin().split()
    index = 0

    N = int(data[index])
    index += 1

    M_G = int(data[index])
    index += 1
    edges_G = []
    for _ in range(M_G):
        u = int(data[index]) - 1
        v = int(data[index + 1]) - 1
        edges_G.append((u, v))
        index += 2

    M_H = int(data[index])
    index += 1
    edges_H = []
    for _ in range(M_H):
        a = int(data[index]) - 1
        b = int(data[index + 1]) - 1
        edges_H.append((a, b))
        index += 2

    A = []
    for i in range(N - 1):
        row = []
        for j in range(N - i - 1):
            row.append(int(data[index]))
            index += 1
        A.append(row)

    return N, M_G, edges_G, M_H, edges_H, A

def is_isomorphic(G, H, perm):
    N = len(G)
    for i in range(N):
        for j in range(i + 1, N):
            if (i, j) in G and (perm[i], perm[j]) not in H:
                return False
            if (i, j) not in G and (perm[i], perm[j]) in H:
                return False
    return True

def min_cost_to_isomorphic(N, M_G, edges_G, M_H, edges_H, A):
    G = set(edges_G)
    H = set(edges_H)
    min_cost = float('inf')

    for perm in permutations(range(N)):
        if is_isomorphic(G, H, perm):
            cost = 0
            for i in range(N):
                for j in range(i + 1, N):
                    if (i, j) in G and (perm[i], perm[j]) not in H:
                        cost += A[i][j - i - 1]
                    if (i, j) not in G and (perm[i], perm[j]) in H:
                        cost += A[i][j - i - 1]
            min_cost = min(min_cost, cost)

    return min_cost

def main():
    N, M_G, edges_G, M_H, edges_H, A = read_input()
    result = min_cost_to_isomorphic(N, M_G, edges_G, M_H, edges_H, A)
    print(result)

if __name__ == "__main__":
    main()
