def min_fatigue_level():
    import sys
    input = sys.stdin.read
    data = input().split()

    N = int(data[0])
    left_hand, right_hand = -1, -1
    fatigue = 0

    for i in range(N):
        key = int(data[2 * i + 1])
        hand = data[2 * i + 2]

        if hand == 'L':
            if left_hand != -1:
                fatigue += abs(key - left_hand)
            left_hand = key
        elif hand == 'R':
            if right_hand != -1:
                fatigue += abs(key - right_hand)
            right_hand = key

    print(fatigue)

