import heapq

def kthSmallestObstacle(queries, k):
    heap = []
    results = []

    for x, y in queries:
        distance = abs(x) + abs(y)
        heapq.heappush(heap, distance)

        if len(heap) < k:
            results.append(-1)
        else:
            results.append(heapq.nsmallest(k, heap)[-1])

    return results

def canReach(grid, health):
    m, n = len(grid), len(grid[0])

    def dfs(x, y, health):
        if x < 0 or x >= m or y < 0 or y >= n or health <= 0:
            return False
        if x == m - 1 and y == n - 1:
            return health >= 1

        temp = grid[x][y]
        grid[x][y] = 2  # Mark as visited

        if dfs(x + 1, y, health - temp) or dfs(x - 1, y, health - temp) or dfs(x, y + 1, health - temp) or dfs(x, y - 1, health - temp):
            return True

        grid[x][y] = temp  # Backtrack
        return False

    return dfs(0, 0, health)

