def minWordsConcatenation(words, target):
    from collections import defaultdict

    # Create a list of prefixes for each word
    prefixes = defaultdict(list)
    for word in words:
        for i in range(1, len(word) + 1):
            prefixes[word[:i]].append(word)

    # Initialize DP array with infinity
    dp = [float('inf')] * (len(target) + 1)
    dp[0] = 0

    # Fill the DP array
    for i in range(1, len(target) + 1):
        for prefix in prefixes:
            if target.startswith(prefix) and i >= len(prefix):
                dp[i] = min(dp[i], dp[i - len(prefix)] + 1)

    # Return the result
    return dp[len(target)] if dp[len(target)] != float('inf') else -1

