    vowels = set('aeiou')
    n = len(word)
    result = 0

    for i in range(n):
        char_count = {}
        vowel_count = 0
        consonant_count = 0

        for j in range(i, n):
            char = word[j]
            if char in vowels:
                vowel_count += 1
            else:
                consonant_count += 1

            char_count[char] = char_count.get(char, 0) + 1

            # Ensure there's at most one of each vowel
            while vowel_count > 5:
                left_char = word[i]
                if left_char in vowels:
                    vowel_count -= 1
                else:
                    consonant_count -= 1

                char_count[left_char] -= 1
                if char_count[left_char] == 0:
                    del char_count[left_char]

                i += 1

            # Check if the current window has exactly k consonants and all vowels
            if vowel_count == 5 and consonant_count == k:
                result += 1

