def minTimeToReduceMountain(mountainHeight, workerTimes):
    def canReduce(height, time):
        total_units = 0
        for workerTime in workerTimes:
            units = 1
            total_time = workerTime
            while total_time <= time:
                total_units += units
                units += 1
                total_time += workerTime * units
        return total_units >= height

    left, right = 1, max(workerTimes) * mountainHeight
    while left < right:
        mid = (left + right) // 2
        if canReduce(mountainHeight, mid):
            right = mid
        else:
            left = mid + 1
    return left

