def kthObstacleDistance(queries, k):
    heap = []
    results = []

    for x, y in queries:
        distance = abs(x) + abs(y)
        heapq.heappush(heap, distance)

        if len(heap) < k:
            results.append(-1)
        else:
            results.append(heapq.heappop(heap))

    return results

def hash_string(s: str, k: int) -> str:
    result = []
    for i in range(0, len(s), k):
        substring = s[i:i+k]
        char_sum = sum(ord(char) - ord('a') for char in substring)
        hashed_char = chr((char_sum % 26) + ord('a'))
        result.append(hashed_char)
    return ''.join(result)

