def transform_string(S, T):
    X = [S]
    queue = [(S, 0)]
    visited = set()
    visited.add(S)

    while queue:
        current, index = heappop(queue)
        if current == T:
            break
        if index < len(S):
            for char in 'abcdefghijklmnopqrstuvwxyz':
                if char != current[index]:
                    new_string = current[:index] + char + current[index+1:]
                    if new_string not in visited:
                        visited.add(new_string)
                        heappush(queue, (new_string, index + 1))
                        X.append(new_string)

    return X

