def shortestPath(n, queries):
    parent = list(range(n))

    def find(x):
        if parent[x] != x:
            parent[x] = find(parent[x])
        return parent[x]

    def union(x, y):
        rootX = find(x)
        rootY = find(y)
        if rootX != rootY:
            parent[rootX] = rootY

    result = []
    current_path_length = n - 1

    for u, v in queries:
        union(u, v)
        if find(0) == find(n - 1):
            current_path_length = min(current_path_length, abs(v - u))
        result.append(current_path_length)

    return result

