def minValidStrings(words, target):
    n = len(target)
    dp = [float('inf')] * (n + 1)
    dp[0] = 0

    for i in range(n):
        for word in words:
            if i + 1 >= len(word) and target[i + 1 - len(word):i + 1] == word:
                dp[i + 1] = min(dp[i + 1], dp[i + 1 - len(word)] + 1)

    return dp[n] if dp[n] != float('inf') else -1

