def can_reach_final_cell(grid, health):
    m, n = len(grid), len(grid[0])

    def dfs(x, y, health):
        if x < 0 or x >= m or y < 0 or y >= n or health <= 0:
            return False
        if x == m - 1 and y == n - 1:
            return health >= 1
        grid[x][y] = -1  # Mark the cell as visited
        directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
        for dx, dy in directions:
            if dfs(x + dx, y + dy, health - grid[x + dx][y + dy]):
                return True
        grid[x][y] = 0  # Unmark the cell
        return False

    return dfs(0, 0, health)

