def maxTowerHeight(maximumHeight):
    n = len(maximumHeight)
    if n > len(set(maximumHeight)):
        return -1

    sorted_heights = sorted(set(maximumHeight), reverse=True)
    assigned_heights = []

    for i in range(n):
        if sorted_heights[i] >= i + 1:
            assigned_heights.append(i + 1)
        else:
            return -1

    return sum(assigned_heights)

