def format_number():
    import sys
    X = float(input().strip())

    # Check if the number is an integer
    if X.is_integer():
        print(int(X))
    else:
        # Format the number to remove trailing zeros
        print(f"{X:.3f}".rstrip('0').rstrip('.'))

