def determine_election_outcome():
    import sys
    input = sys.stdin.read
    data = input().split()

    N = int(data[0])
    T = int(data[1])
    A = int(data[2])

    remaining_votes = N - (T + A)

    if remaining_votes == 0:
        if T > A:
            print("Yes")
        elif A > T:
            print("Yes")
        else:
            print("No")
    elif remaining_votes == 1:
        if T > A:
            print("Yes")
        elif A > T:
            print("Yes")
        else:
            print("No")
    else:
        if T > A:
            print("Yes")
        elif A > T:
            print("Yes")
        else:
            print("No")

determine_election_outcome()

def determine_election_outcome():
    import sys
    input = sys.stdin.read
    data = input().split()

    N = int(data[0])
    T = int(data[1])
    A = int(data[2])

    if T > A:
        # Takahashi is leading
        remaining_votes = N - T
        if T + (remaining_votes // 2) >= N:
            print("Yes")
        else:
            print("No")
    elif A > T:
        # Aoki is leading
        remaining_votes = N - A
        if A + (remaining_votes // 2) >= N:
            print("Yes")
        else:
            print("No")
    else:
        # Tie, outcome not decided yet
        print("No")

